import React from "react";

const emojiList = ["😀", "😃", "😄", "😁", "😆", "😅", "😂", "🤣", "😊", "😇", "🙂", "🙃", "😉", "😌", "😍", "🥰", "😘", "😗", "😙", "😚"];

const RandomEmoji = () => { const randomEmojis = Array.from({ length: 20 }, () => { const randomIndex = Math.floor(Math.random() * emojiList.length); return emojiList[randomIndex]; });

return ( <div> {randomEmojis.map((emoji, index) => ( <span key={index}>{emoji}</span> ))} </div> ); };

export default RandomEmoji;
